import React, { Component } from "react";
import { storeProducts, detailProduct } from "./data";

const AppContext = React.createContext();

class AppProvider extends Component {
  state = {
    products: storeProducts,
    detailProduct: detailProduct,
    modalOpen: false,
    modalProduct: detailProduct
  };

  componentDidMount() {
    this.setProducts();
  }

  setProducts = () => {
    let tempProducts = [];
    storeProducts.forEach(item => {
      //unpacks the values into a single product from the array of storeProduct list data
      const singleItem = { ...item };
      tempProducts = [...tempProducts, singleItem];
    });

    this.setState(() => {
      return { products: tempProducts };
    });
  };

  // getItem retrives the right product from products according to thier id's as a utility method from data.js.
  getItem = id => {
    const product = this.state.products.find(item => item.id === id);
    return product;
  };

  // Handles each product clicked according to thier id's to display in details page
  handleDetail = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { detailProduct: product };
    });
  };

  openModal = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return { modalProduct: product, modalOpen: true };
    });
  };

  closeModal = () => {
    this.setState(() => {
      return { modaleOpen: false };
    });
  };

    addToCart = id => {
     let tempProducts = [...this.state.products];
     const index = tempProducts.indexOf(this.getItem(id));
     const product = tempProducts[index];
     product.inCart = true;
     product.count = 1;
     const price = product.price;
     product.total = price;

     this.setState(
      () => {
       return {
        products: tempProducts,
        cart: [...this.state.cart, product]
       };
      },
      () => {
       this.addTotal(); // through this callback function each time we add items to our cart the values for cartSubTotal, cartTax, cartTotal will update dynamically
      }
     );
    };

  addToCart = () => {
    console.log("hello from cart");
  };
  render() {
    return (
      <AppContext.Provider
        value={{
          ...this.state,
          handleDetail: this.handleDetail,
          openModal: this.openModal,
          closeModal: this.closeModal,
          addToCart: this.handleDetail
        }}
      >
        {this.props.children}
      </AppContext.Provider>
    );
  }
}

const AppConsumer = AppContext.Consumer;
export { AppProvider, AppConsumer };
