import React, { Component } from "react";
import { NavLink } from "react-router-dom";

class Navigation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isExpanded: false
    };
  }
  handleToggle(e) {
    e.preventDefault();
    this.setState({
      isExpanded: !this.state.isExpanded
    });
  }

  closeMenu() {
    this.setState({ isExpanded: false });
  }

  render() {
    const { isExpanded } = this.state;

    return (
      <header id={"header"}>
        <NavLink to="/" className="logo">
          <img src="/img/logo.pngg" alt="" />
        </NavLink>
        {/* Menu Icon /> */}
        <div className="menu-icon">
          <i onClick={e => this.handleToggle(e)} className={"fas fa-bars"} />
        </div>
        {/* Navigation*/}
        <div className={`menu collapsed ${isExpanded ? "is-expanded" : ""}`}>
          <nav
            onStateChange={state => this.closeMenu(state)}
            className={"cl-effect-14"}
          >
            <NavLink to="/"> Home </NavLink>
            <NavLink to="./ProductList"> Products </NavLink>
            <NavLink to="./Blog"> Blog </NavLink>
            <NavLink to="./About"> About </NavLink>
          </nav>{" "}
        </div>{" "}
      </header>
    );
  }
}

export default Navigation;
