import React, {
 Component
} from "react";
import {
 storeProducts,
 detailProduct
} from "./data";

const ProductContext = React.createContext(); //(ReactContext) provider && consumer

class ProductProvider extends Component {
 state = {
  products: [],
  detailProduct: detailProduct,
  cart: [],
  modalOpen: false,
  modalProduct: detailProduct,
  cartSubTotal: 0,
  cartTax: 0,
  cartTotal: 0
 };

 componentDidMount() {
  this.setProducts();
 }

 setProducts = () => {
  let tempProducts = [];
  storeProducts.forEach(item => {
   //unpacks the values into a single product from the array of storeProduct list data
   const singleItem = {
    ...item
   };
   tempProducts = [...tempProducts, singleItem];
  });
  this.setState(() => {
   return {
    products: tempProducts
   };
  });
 };

 // getItem retrives the right product from products according to thier id's as a utility method from data.js.
 getItem = id => {
  const product = this.state.products.find(item => item.id === id);
  return product;
 };

 // Handles each product clicked according to thier id's to display in details page
 handleDetail = id => {
  const product = this.getItem(id);
  this.setState(() => {
   return {
    detailProduct: product
   };
  });
 };

 addToCart = id => {
  let tempProducts = [...this.state.products];
  const index = tempProducts.indexOf(this.getItem(id));
  const product = tempProducts[index];
  product.inCart = true;
  product.count = 1;
  const price = product.price;
  product.total = price;

  this.setState(
   () => {
    return {
     products: tempProducts,
     cart: [...this.state.cart, product]
    };
   },
   () => {
    this.addTotal(); // through this callback function each time we add items to our cart the values for cartSubTotal, cartTax, cartTotal will update dynamically
   }
  );
 };

 openModal = id => {
  const product = this.getItem(id);
  this.setState(() => {
   return {
    modalProduct: product,
    modalOpen: true
   };
  });
 };

 closeModal = () => {
  this.setState(() => {
   return {
    modalOpen: false
   };
  });
 };

 incrementCart = id => {
  let tempCart = [...this.state.cart];
  const selectedProduct = tempCart.find(item => item.id === id);
  const index = tempCart.indexOf(selectedProduct);
  const product = tempCart[index];
  product.count = product.count + 1;
  product.total = product.count * product.price;

  this.setState(
   () => {
    return {
     cart: [...tempCart]
    };
   },
   () => {
    this.addTotal();
   }
  );
 };
 decrementCart = id => {
  let tempCart = [...this.state.cart];
  const selectedProduct = tempCart.find(item => item.id === id);
  const index = tempCart.indexOf(selectedProduct);
  const product = tempCart[index];
  product.count = product.count - 1;

  if (product.count === 0) {
   this.removeItem(id);
  } else {
   product.total = product.count * product.price;
   this.setState(
    () => {
     return {
      cart: [...tempCart]
     };
    },
    () => {
     this.addTotal();
    }
   );
  }
 };

 removeItem = id => {
  let tempProducts = [...this.state.products];
  let tempCart = [...this.state.cart];

  const index = tempProducts.indexOf(this.getItem(id));
  let removedProduct = tempProducts[index];
  removedProduct.inCart = false;
  removedProduct.count = 0;
  removedProduct.total = 0;

  tempCart = tempCart.filter(item => item.id !== id);

  this.setState(
   () => {
    return {
     cart: [...tempCart],
     products: [...tempProducts]
    };
   },
   () => {
    this.addTotal();
   }
  );
 };

 clearCart = () => {
  this.setState(
   () => {
    return {
     cart: []
    };
   },
   () => {
    // N.B. callback function
    this.setProducts();
    this.addTotal();
   }
  );
 };

 addTotal = () => {
  let subTotal = 0;
  this.state.cart.map(item => (subTotal += item.total));

  const tempTax = subTotal * 0.1; // 0.1 as tax is going to be 10%
  const tax = parseFloat(tempTax.toFixed(2));
  const total = subTotal + tax;
  this.setState(() => {
   return {
    cartSubTotal: subTotal,
    cartTax: tax,
    cartTotal: total
   };
  });
 };

 render() {
  return ( <
   ProductContext.Provider // ProductContext.Provider sits on top of component tree
   value = {
    {
     ...this.state,
     handleDetail: this.handleDetail,
     addToCart: this.addToCart,
     openModal: this.openModal,
     closeModal: this.closeModal,
     incrementCart: this.incrementCart,
     decrementCart: this.decrementCart,
     removeItem: this.removeItem,
     clearCart: this.clearCart
    }
   } >
   {
    this.props.children
   } <
   /ProductContext.Provider>
  );
 }
}

const ProductConsumer = ProductContext.Consumer;
export {
 ProductProvider,
 ProductConsumer
};

